double neu_func(double w,double x,double w2,double x2,double b,double w0)
{
    double y;
    y=(w*x)+(w2*x2)+w0;
    if(y>0)
    {
           y=1;
    }
    else if(y<0)
    {
         y=-1;
     }
    return y;
}

